import apiClient from './api';

// 獲取所有員工資料
export const getAllEmployees = async () => {
  try {
    return await apiClient.get('/employees');
  } catch (error) {
    console.error('Error fetching employees:', error);
  }
};

// 根據員工 ID 查詢員工資料
export const getEmployeeById = async (empId) => {
  try {
    return await apiClient.get(`/employees/${empId}`);
  } catch (error) {
    console.error('Error fetching employee by ID:', error);
  }
};

// 新增員工資料
export const createEmployee = async (employee) => {
  try {
    return await apiClient.post('/employees', employee);
  } catch (error) {
    console.error('Error creating employee:', error);
  }
};

// 更新員工資料
export const updateEmployee = async (empId, employee) => {
  try {
    return await apiClient.put(`/employees/${empId}`, employee);
  } catch (error) {
    console.error('Error updating employee:', error);
  }
};

// 刪除員工資料
export const deleteEmployee = async (empId) => {
  try {
    return await apiClient.delete(`/employees/${empId}`);
  } catch (error) {
    console.error('Error deleting employee:', error);
  }
};
