import apiClient from './api';

// 獲取所有座位資料
export const getAllSeatingCharts = async () => {
  try {
    return await apiClient.get('/seating-charts');
  } catch (error) {
    console.error('Error fetching seating charts:', error);
  }
};

// 根據座位序號查詢座位資料
export const getSeatingByFloorSeatSeq = async (floorSeatSeq) => {
  try {
    return await apiClient.get(`/seating-charts/${floorSeatSeq}`);
  } catch (error) {
    console.error('Error fetching seating by floor seat seq:', error);
  }
};

// 新增座位資料
export const createSeatingChart = async (seatingChart) => {
  try {
    return await apiClient.post('/seating-charts', seatingChart);
  } catch (error) {
    console.error('Error creating seating chart:', error);
  }
};

// 更新座位資料
export const updateSeatingChart = async (floorSeatSeq, seatingChart) => {
  try {
    return await apiClient.put(`/seating-charts/${floorSeatSeq}`, seatingChart);
  } catch (error) {
    console.error('Error updating seating chart:', error);
  }
};

// 刪除座位資料
export const deleteSeatingChart = async (floorSeatSeq) => {
  try {
    return await apiClient.delete(`/seating-charts/${floorSeatSeq}`);
  } catch (error) {
    console.error('Error deleting seating chart:', error);
  }
};
